import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";
import SearchIcon from "@material-ui/icons/Search";
import axios from "axios";
import Card from "./Card";

function SearchArea(props) {


    const [photo, setPhoto] = useState("");
    const [clientId, setClientId] = useState("YYYw7oE7qPoGGH0TZb3J-h7stckt87pGkvD3-019_bk#");
    const [answer, setAnswer] = useState([]);

    function handleChange(event) {
        setPhoto(event.target.value);
    }

    function handleClick() {
        const url = "https://api.unsplash.com/search/photos?query=" + photo + "&client_id=" + clientId;

        axios.get(url).then((response) => {
            console.log(response);
            setAnswer(response.data.results);
        });
    }
    const handleKeypress = e => {
        //it triggers by pressing the enter key
      if (e.keyCode === 13) {
        handleClick();
      }
    };



    return (
        <div className="SearchArea">
            <div className="container h-100">
                <div className="row h-100 justify-content-center align-items-center"></div>
                <InputGroup onChange={handleChange} className="col-6">
                    <FormControl
                        placeholder="Search Anything!"
                        aria-label="Search"
                        aria-describedby="basic-addon2"
                    />
                    <Button variant="outline-secondary" id="button-addon2" onKeyPress={handleKeypress} onClick={handleClick}>
                        <SearchIcon />
                    </Button>
                    
                    {answer.map((photo) => (
                        <img alt="" src={photo.urls.small} />
                    ))}
                    
                </InputGroup>
            </div>
        </div>
    );
}

export default SearchArea;
